<?php
// Hex string from HttpCanary (you need to replace this with the actual hex string)
$hex = "_OGOGOGO";

function hexToBin($hex) {
    $bin = '';
    for ($i = 0; $i < strlen($hex); $i += 2) {
        $bin .= chr(hexdec(substr($hex, $i, 2)));
    }
    return $bin;
}

// AES Decrypt function
function aesDecrypt($ciphertext, $key, $iv) {
    $decrypted = openssl_decrypt($ciphertext, 'aes-256-cbc', $key, OPENSSL_RAW_DATA, $iv);
    return $decrypted;
}

// Hexadecimal values for ciphertext, key, and iv
$ciphertextHex = "b8d06aafadd904c1fb2c7ebf430aafca495226f5d300eafe1664a8e4557c28e76c6c78507fb7b64d5da360dbafb5660e3b9fe432ff4671378abbb01084d9622e733bb987260d21a891a67c8d41ef2e1b6505b7d40ac8dc0d270d25b6abd62d66";
$keyHex = "3839303337373437343731333631333435373638383033303535373534373037";
$ivHex = "52347467686a675e3432352840234767";

// Convert hex to binary data
$ciphertext = hexToBin($ciphertextHex);
$key = hexToBin($keyHex);
$iv = hexToBin($ivHex);

// Decrypt the ciphertext
$decryptedResponse = aesDecrypt($ciphertext, $key, $iv);

// Output the decrypted response
echo "Decrypted Response: " . $decryptedResponse;

